"use strict";function signup(){var e=$("#signupForm");e.validate(),$("#signup").click(function(a){if(a.preventDefault(),e.valid()){var i=$("#signupForm").serialize();$.ajax({url:"/signup",data:i,method:"POST",success:function(){document.location="/home"}})}})}function validate(){$("#signupForm").validate({rules:{name:"required",password:"required",email:{required:!0,email:!0},phone:{required:!0}},messages:{name:"Please enter your name",email:"Please enter a valid email address",phone:"Please enter your phone",password:"Please enter a valid password"}})}$(document).ready(function(){validate(),signup()});